WebLink.url:
{!REQUIRESCRIPT("/soap/ajax/15.0/connection.js")}
{!REQUIRESCRIPT("/soap/ajax/15.0/apex.js")}

// Now make a synchronous call to the Apex Web service method to update release burndown

try {
  var result = sforce.connection.query("select id,Name, Build__c,Scrum_Team__c from ADM_Team_Release__c where id = '{!ADM_Team_Release__c.Id}' ");
  if (result .size <= 0) {throw ("Error: Unable to retrieve team release record");}
  var teamReleaseArray  = result.getArray("records");

  var result = sforce.apex.execute(
  "ADM_ReleaseBurndownUtils", // class
  "updateReleaseBurndownForTeamReleases", // method
  {teamReleaseList: teamReleaseArray}); // method arguments

  location.reload(true);

} catch(error) {
  alert(error);
}
