WebLink.url:
{!REQUIRESCRIPT("/soap/ajax/15.0/connection.js")}
{!REQUIRESCRIPT("/soap/ajax/15.0/apex.js")}

var chatterValue = sforce.apex.execute("ADM_Chatter","toggleSubscription", {parentId:"{!ADM_Work__c.Id}",subscriberId:"{!User.Id}"});

var buttons = document.getElementsByTagName('input');

for(var i = 0; i < buttons.length; i++) {
  var button = buttons[i];
  if(button.name == 'follow') {
    if(chatterValue.indexOf('+ Follow') > -1 || chatterValue.indexOf('- Unfollow') > -1) {
      button.value = chatterValue;
      button.title = chatterValue;
    } else {
      alert(chatterValue);
      break;
    }
  }
}
