WebLink.url:
var subject = "Work record {!ADM_Work__c.Name} - {!SUBSTITUTE(ADM_Work__c.Subject__c,'"','\'')}";

var text2copy = unescape(subject);



text2copy = text2copy.replace("&amp;", "&");

text2copy = text2copy.replace("&gt;", ">");

text2copy = text2copy.replace("&lt;", "<");



var body = escapeUTF ( window.location.href );

document.location = "mailto:{!User.Email}?subject=" + text2copy + "&body=" + body;
