WebLink.url:
var selectedIds= getRecordIds();
var queryString = "";
var pageDestination = "/apex/GenerateAnnotations?cases=";

if (selectedIds.length == 0){
  alert("Please select at least one test case to generate annotations for");
} else {
  for (var i in selectedIds){
    var tcID = selectedIds[i];
    if (typeof(tcID) != "string")continue;
    queryString += tcID + ";";
  }
  queryString = queryString.substring(0, queryString.length-1);
  window.location.href=pageDestination+queryString;
}
