{!REQUIRESCRIPT ("/soap/ajax/13.0/connection.js")} 
var records = {!GETRECORDIDS($ObjectType.eRelease_Request__c)};
var newRecords = []; 
if (records[0] == null) { 
	alert("Please select at least one row.") 
} else {
	for (var n=0; n<records.length; n++) {
		var c = new sforce.SObject("eRelease_Request__c"); 
		c.id = records[n]; 
		c.eRelease_Status__c = "Released";
		newRecords.push(c); 
	} 
	result = sforce.connection.update(newRecords); 
	window.location.reload(); 
}
